xwikidoc.content:
{{groovy}}
package org.xwiki.activiti.internal;

import org.activiti.engine.delegate.DelegateExecution;
import org.activiti.engine.delegate.DelegateTask;
import org.activiti.engine.delegate.TaskListener;

public class TaskListenerCustom implements TaskListener
{
    /**
     * 
     */
    private static final long serialVersionUID = 1L;

    @Override
    @SuppressWarnings("unchecked")
    public void notify(DelegateTask delegateTask)
    {
        DelegateExecution execution = delegateTask.getExecution();

        System.out.println("Username " + execution.getVariable("Username"));

        System.out.println("Document Name " + execution.getVariable("Document Name"));

        System.out.println("Document Space " + execution.getVariable("Document Space"));

        delegateTask.setAssignee((String) execution.getVariable("Username"));
        delegateTask.setDescription("Fill article information for document " + execution.getVariable("Document Name"));
        System.out.println("Dynamically assigning Task to " + delegateTask.getAssignee());

    }
}
{{/groovy}}
